package com.smol.citt

import android.util.Log
import com.google.gson.Gson
import kotlin.random.Random

operator fun String.invoke(vararg answers: String): Question {
    return Question(this, answers.toMutableList())
}

class QuestionSet(
    val questions: MutableList<Question>
) {

    val numQuestions: Int get() = questions.size


    public operator fun get(x: Int) = questions[x]
    public operator fun <T> List<T>.iterator() = questions.iterator()


    /*operator fun plusAssign(b: iStrPA) {
        this.questions.add(Question(b, null))
        Log.d("TAG", "plusAssign: asdfasdf")
    }
     */

    operator fun plusAssign(b: Question) {
        this.questions.add(b)
    }

    fun randomQuestion(): Question = this[Random.nextInt(0, questions.size - 1)]

    override fun toString(): String = toJson()


    private fun toJson() = Gson().toJson(this)
        .replace(",", ",\n")
        .replace("{", "\n{ ")
        .replace("}", " }")
        .replace("\"a\":", "\"a\":\n")
        .replace("\"q\":", "\"q\":\n")


}



